import './App.css';
import styled from 'styled-components'
import { useEffect, useState } from 'react';

const Title = styled.h1`
  padding : 20px;
  color : tomato;
`;

function App() {
  var output = document.getElementById("out");

  if (!navigator.geolocation) {
    output.innerHTML = '<p>사용자의 브라우저는 지오로케이션을 지원하지 않습니다.</p>'
    return;
  }
  const [location, setLocation] = useState({});

  const success = (position) => {
    const latitude = position.coords.latitude;
    const longitude = position.coords.longitude;
    output.innerHTML = '<p>위도 : ' + latitude + '° <br>경도 : ' + longitude + '°</p>';
    var img = new Image();
    img.src = 'http://maps.googleapis.com/maps/api/staticmap?'
      + 'center=' + latitude + ',' + longitude + '&zoom=13&size=300x300&sensor=false';
    output.appendChild(img);
  }

  const error = () => {
    output.innerHTML = "사용자의 위치를 찾을 수 없습니다.";
  };
  output.innerHTML = "<p>Locating...</p>";
  navigator.geolocation.getCurrentPosition(success, error);

  // useEffect(() => {
  //   setLocation()
  // }, [location])

  return (
    <div className="App">
      <Title>안녕하세요 wooga 팀입니다.</Title>
    </div>
  );
}

export default App;
